definition.script:
pipeline {
    agent any

    environment {
        WORKSPACE_DIR = '/workspace'
        WORKSPACE_APP_DIR = '/workspace/apps/springboot-context-jdk24'
        VALIDATION_COMMAND = 'mvn clean install -B'
    }

    stages {
        stage('Checkout') {
            steps {
                dir("${env.WORKSPACE_DIR}") {
                    script {
                        sh '''
                            mkdir -p apps
                            cd apps
                            rm -rf springboot-context-jdk24
                            git clone --verbose https://github.com/codegik/springboot-context-jdk24.git
                            ls -la springboot-context-jdk24/
                        '''
                    }
                }
            }
        }

        stage('All Steps') {
            steps {
                dir("${env.WORKSPACE_APP_DIR}") {
                    script {
                        sh "${env.VALIDATION_COMMAND}"
                    }
                }
            }
            post {
                failure {
                    dir("${env.WORKSPACE_DIR}") {
                        script {
                            def jobOutput = currentBuild.rawBuild.getLog(1000).join('\n')
                            def cleanOutput = jobOutput.replaceAll(/\x1B\[[;0-9]*[a-zA-Z]/, '').replaceAll(/\[\[0;?[0-9;]*m/, '')

                            writeFile file: '/tmp/job_output.txt', text: cleanOutput

                            sh "echo 'Build failed. Calling agent to fix the issues...'"
                            sh "chmod +x agent.py"
                            sh "python -u agent.py ${WORKSPACE_APP_DIR} /tmp/job_output.txt '${env.VALIDATION_COMMAND}'"
                        }
                    }
                }
            }
        }
    }
}
